import { useState } from "react";

function CmpRegistration(){
    let [formData,setFormData]=useState({
        full_name:'',
        company_name:'',
        cmp_phone:'',
        cmp_address:'',
        email:'',
        password:''


    });


    const save_registration=(e)=>{
        e.preventDefault();
        const data=new FormData();
        data.append("full_name",formData.full_name);
        data.append("company_name",formData.company_name);
        data.append("cmp_phone",formData.cmp_phone);
        data.append("cmp_address",formData.cmp_address);
        data.append("email",formData.email);
        data.append("password",formData.password);

        fetch('http://localhost/react_project/save_registration.php',{
          method:"POST",
       
          body:data
        }
        )
        .then((result)=>{return result.json();
      
        })
        .then((data)=>{
          if(data.Status)
          {
            alert("successfully saved")
            
          }
          else
          {
            alert(data.message)
          }
        })
      
      }
      const handleChange=(e)=>{
        let name=e.target.name;
              let value=e.target.value
              setFormData({
                  ...formData,
                  [name]: value
              });
      
      }
    return (
        <>
        {JSON.stringify(formData)} 
        <h1>Registration</h1>
        <div class="card">
             <div class="class-header">
                
             </div>
             <div class="card-body">
             <form class="row g-3" onSubmit={save_registration}>
                    <div class="form-group">
                        <label>Full Name</label>
                        <input onChange={handleChange} type="text" class="form-control" name="full_name" />
                    </div>
                    <div class="form-group">
                        <label>Company Name</label>
                        <input onChange={handleChange} type="text" class="form-control" name="company_name" />
                    </div>
                    <div class="form-group">
                        <label>Phone Number</label>
                        <input onChange={handleChange}type="text" class="form-control" name="cmp_phone" />
                    </div>
                    <div class="form-group">
                        <label>Address</label>
                        <input onChange={handleChange} type="text" class="form-control" name="cmp_address" />
                    </div>
                    <div class="form-group">
                        <label>Email Id</label>
                        <input onChange={handleChange} type="text" class="form-control" name="email" />
                    </div>
                    <div class="form-group">
                        <label>Password</label>
                        <input onChange={handleChange} type="password" class="form-control" name="password" />
                    </div>
                    
                    <div class="form-group pt-2">
                        
                    <button type="submit" class="btn btn-primary mb-3">Save</button>
                    </div>
                 </form>
             </div>
             </div>
        </>
    )
}
export default CmpRegistration;
