import { useState } from "react";

function CmpLogin(){
    let [formData,setFormData]=useState({
        username:'',
        password:''


    });


    const get_login=(e)=>{
        e.preventDefault();
        const data=new FormData();
        data.append("username",formData.username);
        
        data.append("password",formData.password);
        fetch('http://localhost/kcmt_job_board/get_login.php',{
          method:"POST",
       
          body:data
        }
        )
        .then((result)=>{return result.json();
      
        })
        .then((data)=>{
          if(data.Status=="true")
          {
            alert("successfully saved")
            
          }
          else
          {
            alert(data.message)
          }
        })
      
      }
      const handleChange=(e)=>{
        let name=e.target.name;
              let value=e.target.value
              setFormData({
                ...formData,
                [name]: value
            });
    
    }
  return (
      <>
      
      <h1>Login</h1>
      <div class="card">
           <div class="class-header">
              
           </div>
           <div class="card-body">
           <form class="row g-3" onSubmit={get_login}>
                  <div class="form-group">
                      <label>Username</label>
                      <input onChange={handleChange} type="text" class="form-control" name="username" />
                  </div>
                  
                    <div class="form-group">
                        <label>Password</label>
                        <input onChange={handleChange} type="password" class="form-control" name="password" />
                    </div>
                    <div class="form-group pt-2">
                        
                    <button type="submit" class="btn btn-primary mb-3">Login</button>
                    </div>
                 </form>
             </div>
             </div>
        </>
    )
}
export default CmpLogin;
