import React, { useEffect, useState } from 'react'

const CmpCategory = () => {

const[clist,setCategoryList] = useState([]);

const getallCategory =() =>{
  fetch('http://localhost/react_project/get_category.php?')
  .then(result =>{
    return result.json();
  }).then(responds=>{
    setCategoryList(responds);

  })
}
useEffect(()=>{
  getallCategory()
},[])

return

  return (
    <>   
    <div class="row">
        <div class="col-12">
            <div class="card">
                <div class="card-header">
                  <h5>CATEGORY</h5>
                </div>
                <div class="card-body">
                <form class="row g-3">
     <div class="col-auto">
    <label for="cname">CATEGORY NAME</label>
    <input type="text" class="form-control" id="inputPassword2" placeholder="category name"/>
  </div>
  <div class="col-auto">
    <button type="submit" class="btn btn-primary mb-3">SAVE</button>
  </div>
</form>
                </div>
                </div>
                </div>
                </div>

</>
  )
}

export default CmpCategory
