import React from 'react'

const CmpRegistration = () => {

    const[formData,setFormData]=useState({
        cname:' '
    })
    const getallCategory =()=>{
        fetch('http://localhost/react_project/get_category.php')
        .then(result =>{
            return result.json();
        }).then(responds=>{
            setCategory(responds)
        })
    }
    useEffect(()=>{
        getallCategory()
    },[]) 
    const  save_category=(e)=>{
        e.preventDefault();
        const data=new FormData();
        data.append("cname",  formData.cname);
        fetch('http://localhost/react_project/save_category.php',{
            method:"POST",
            body: data
        })
        .then((result)=>{
            return result.json();
        })
        .then((data)=>{
            if(data.Status){
                getallCategory();
            }else{
                alert(data.Message)
            }
        })
    }
    const handle_change=(e)=>{
        let name=e.target.name;
        let value=e.target.value;
        setFormData({
            ...formData,
            [name]: value,
        });
    }
    const  delete_category=(cid)=>{
        const data=new FormData();
        data.append("cid",  cid);
        fetch('http://localhost/react_project/delete_category.php',{
            method:"POST",
            body: data
        })
        .then((result)=>{
            return result.json();
        })
        .then((data)=>{
            if(data.Status){
                getallCategory();
            }else{
                alert(data.Message)
            }
        })
    }
    
        












  return (




    <div class="container">
        <div class="row">
        <div class="col-md-6">
<h2>Registration</h2>
<form>
    <div class="form-group">
        <label>Full Name</label>
        <input type='text' name="FullName" class="form-control" />
    </div>
    <div class="form-group">
        <label>Company Name</label>
        <input type='text' name="Company Name" class="form-control" />
    </div>
    <div class="form-group">
        <label>Company Phone</label>
        <input type='text' name="Company Phone" class="form-control" />
    </div>
    <div class="form-group">
        <label>Company Address</label>
        <textarea name="Company Address" class="form-control" />
        
    </div>
    <div class="form-group">
        <label>Email</label>
        <input type='text' name="Email" class="form-control" />
    </div>
    <div class="form-group">
        <label>Password</label>
        <input type='text' name="Password" class="form-control" />
    </div>
 <div class="col-auto">
  <button type="submit" class="btn btn-success mb-3">Submit</button>
</div>
</form>
    </div>
    </div>
    </div>
  )
}

export default CmpRegistration;
