import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const ShowUserSingleBill = () => {
  const { echs_member_id } = useParams(); // Get the ECHS member ID from the URL

  const [memberData, setMemberData] = useState({
    official_name: "",
    echs_card_no: "",
    bill_month: "",
    bill_year: "",
    bank_ifsc_code: "",
    bank_account_no: "",
    aadhar_no: "",
    total_amount: "",
    bills: [], // Array for bill items
  });

  const [error, setError] = useState(null);

  // Fetch existing member details
  useEffect(() => {
    const fetchMemberDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost/echs_app/getsingleuserbill.php?echs_member_id=${echs_member_id}`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch member details");
        }
        const data = await response.json();
        setMemberData(data); // Set fetched data directly
      } catch (err) {
        setError(err.message);
      }
    };

    fetchMemberDetails();
  }, [echs_member_id]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container">
      <h1>Single User Bill Details</h1>
      <div>
        <p><strong>Official Name:</strong> {memberData.official_name || "N/A"}</p>
        <p><strong>Echs Card No:</strong> {memberData.echs_card_no || "N/A"}</p>
        <p><strong>Bill Month:</strong> {memberData.bill_month || "N/A"}</p>
        <p><strong>Bill Year:</strong> {memberData.bill_year || "N/A"}</p>
        <p><strong>Bank Account No:</strong> {memberData.bank_account_no || "N/A"}</p>
        <p><strong>Bank IFSC Code:</strong> {memberData.bank_ifsc_code || "N/A"}</p>
        <p><strong>Aadhar No:</strong> {memberData.aadhar_no || "N/A"}</p>
        <p><strong>Total Amount:</strong> {memberData.total_amount || "N/A"}</p>
      </div>

      <h2>Bill Items</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Bill No</th>
            <th>Bill Date</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          {memberData.bills.length > 0 ? (
            memberData.bills.map((bill, index) => (
              <tr key={index}>
                <td>{bill.billNo || "N/A"}</td>
                <td>{bill.billDate || "N/A"}</td>
                <td>{bill.amount.toFixed(2) || "N/A"}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No bill items available.</td>
            </tr>
          )}
        </tbody>
        {memberData.total_amount && (
          <tfoot>
            <tr>
              <td colSpan="2"><strong>Total:</strong></td>
              <td><strong>{memberData.total_amount.toFixed(2)}</strong></td>
            </tr>
          </tfoot>
        )}
      </table>
    </div>
  );
};

export default ShowUserSingleBill;
