import React from 'react'
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
//iimport './CmpStaffupdate.css';

 

const MemberUpdate = () => {
  const { echs_member_id } = useParams(); // Get the ad ID from the URL
  const navigate = useNavigate(); // To redirect after successful update
  const [memberData, setmemberData] = useState({
    official_name: "",
    cmp_address: "",
    cmp_phone: "",
    email: "",
    gender: "",
    rank: "",
    echs_card_no: "",
    aadhar_no: "",
    bank_account_no: "",
    bank_ifsc_code: "",
    password:""
  });

  const [error, setError] = useState(null);

  // Fetch existing ad details
  useEffect(() => {
    const fetchMemberDetails = async () => {
      try {
        const response = await fetch(`http://localhost/echs_app/getmemberbyid.php?echs_member_id=${echs_member_id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch staff details");
        }
        const data = await response.json();
        setmemberData({
          official_name: data.official_name,
          cmp_address: data.cmp_address,
          cmp_phone: data.cmp_phone,
          email: data.email,
          gender: data.gender,
          rank: data.rank,
          echs_card_no: data.echs_card_no,
          aadhar_no: data.aadhar_no,
          bank_account_no: data.bank_account_no,
          bank_ifsc_code: data.bank_ifsc_code,
          password: data.password,
        });
      } catch (err) {
        setError(err.message);
      }
    };

    fetchMemberDetails();
  }, [echs_member_id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setmemberData({ ...memberData, [official_name]: value });
  };


  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    //formData.append("staff_id", staff_id);
    formData.append("official_name", formData.official_name);
    formData.append("cmp_address", formData.cmp_address);
    formData.append("cmp_phone", formData.cmp_phone);
    formData.append("email", formData.email);
    formData.append("gender", formData.gender);
    formData.append("rank", formData.rank);
    formData.append("echs_card_no", formData.echs_card_no);
    formData.append("aadhar_no", formData.aadhar_no);
    ata.append("bank_account_no", formData.bank_account_no);
    data.append("bank_ifsc_code", formData.bank_ifsc_code);
    data.append("password", formData.password);


 

    try {
      const response = await fetch(`http://localhost/echs_app/update_member.php?echs_member_id=${echs_member_id}`, {
        method: "POST",
        body: formData,
      });
      const result = await response.json();

      if (result.Status) {
        alert("Member updated successfully");
        navigate("/admin/Member"); // 
      } else {
        alert(result.message);
      }
    } catch (error) {
      alert("Error updating the ad: " + error.message);
    }
  };

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div class="container">
      <h1>Edit Staff</h1>
      <form onSubmit={handleSubmit}>
        
        <div>
          <label>Offcial Name:</label>
          <input class="form-control"

            type="text"
            name="offcial_name"
            value={memberData.offcial_name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Address:</label>
          <input class="form-control"

            type="text"
            name="cmp_address"
            value={memberData.cmp_address}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Phone no:</label>
          <input class="form-control"

            type="text"
            name="cmp_phone"
            value={memberData.cmp_phone}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input class="form-control"

            type="text"
            name="email"
            value={memberData.email}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Gender:</label>
          <input class="form-control"

            type="text"
            name="gender"
            value={memberData.gender}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Rank:</label>
          <input class="form-control"

            type="text"
            name="rank"
            value={memberData.rank}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>ECHS CARD no:</label>
          <input class="form-control"

            type="text"
            name="echs_card_no"
            value={memberData.echs_card_no}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>aadhar no:</label>
          <input class="form-control"

            type="text"
            name="aadhar_no"
            value={memberData.aadhar_no}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Bank Account No:</label>
          <input class="form-control"

            type="text"
            name="bank_account_no"
            value={memberData.bank_account_no}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Bank IFSC code:</label>
          <input class="form-control"

            type="text"
            name="bank_ifsc_code"
            value={memberData.bank_ifsc_code}
            onChange={handleInputChange}
            required
          />
        </div>
        
        <div>
          <label>Password:</label>
          <input class="form-control"

            type="password"
            name="password"
            value={memberData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        
        <button type="submit">Update</button>
      </form>
    
    </div>
  
  );
};

export default MemberUpdate;
