import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CmpLogin = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const navigate = useNavigate();

  // Handle input changes
  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Check login credentials
  const check_login = async (e) => {
    e.preventDefault();
  
    const data = new FormData();
    data.append("username", formData.username);
    data.append("password", formData.password);
  
    try {
      const response = await fetch('http://localhost/echs_app/get_login.php', {
        method: "POST",
        body: data
      });
  
      const loginData = await response.json(); // Make sure this is awaited
  
      if (loginData.Status === "true") {
        // Check the user type for redirection
        if (loginData.user && loginData.user.usertype === "admin") {
          alert("Welcome, Admin!");
          navigate('/userDashboard');  // Admin Home Page
        } else if (loginData.user && loginData.user.usertype === "staff") {
          alert("Welcome, Staff User!");
          localStorage.setItem("username", formData.username);
          navigate('/staffDashboard');  // Staff Home Page
        } else if (loginData.user && loginData.user.usertype === "user") {
          alert("Welcome, User!");
          localStorage.setItem("username", formData.username);
          navigate('/userDashboard');  // User Home Page
        } else {
          alert("Unknown user type!");
        }
      } else {
        alert(loginData.Message);  // Display error message if login fails
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("An error occurred. Please try again later.");
    }
  };
  

  return (
    <>
      <link rel="icon" type="image/png" href="images/icons/favicon.ico" />
      <link rel="stylesheet" type="text/css" href="vendor/bootstrap/css/bootstrap.min.css" />
      <link rel="stylesheet" type="text/css" href="fonts/font-awesome-4.7.0/css/font-awesome.min.css" />
      <link rel="stylesheet" type="text/css" href="fonts/Linearicons-Free-v1.0.0/icon-font.min.css" />
      <link rel="stylesheet" type="text/css" href="vendor/animate/animate.css" />
      <link rel="stylesheet" type="text/css" href="vendor/css-hamburgers/hamburgers.min.css" />
      <link rel="stylesheet" type="text/css" href="vendor/animsition/css/animsition.min.css" />
      <link rel="stylesheet" type="text/css" href="vendor/select2/select2.min.css" />
      <link rel="stylesheet" type="text/css" href="vendor/daterangepicker/daterangepicker.css" />
      <link rel="stylesheet" type="text/css" href="css/util.css" />
      <link rel="stylesheet" type="text/css" href="css/main.css" />

      <div className="limiter">
        <div className="container-login100">
          <div className="wrap-login100 p-t-30 p-b-50">
            <span className="login100-form-title p-b-41">
              Login
            </span>
            <form className="login100-form validate-form p-b-33 p-t-5" onSubmit={check_login}>
              <div className="wrap-input100 validate-input" data-validate="Enter username">
                <input
                  onChange={handleChange}
                  className="input100"
                  type="text"
                  name="username"
                  placeholder="Username"
                  value={formData.username}
                />
                <span className="focus-input100" data-placeholder="&#xe82a;"></span>
              </div>

              <div className="wrap-input100 validate-input" data-validate="Enter password">
                <input
                  onChange={handleChange}
                  className="input100"
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={formData.password}
                />
                <span className="focus-input100" data-placeholder="&#xe80f;"></span>
              </div>

              <div className="container-login100-form-btn m-t-32">
                <button className="login100-form-btn">
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      <div id="dropDownSelect1"></div>

      <script src="vendor/jquery/jquery-3.2.1.min.js"></script>
      <script src="vendor/animsition/js/animsition.min.js"></script>
      <script src="vendor/bootstrap/js/popper.js"></script>
      <script src="vendor/bootstrap/js/bootstrap.min.js"></script>
      <script src="vendor/select2/select2.min.js"></script>
      <script src="vendor/daterangepicker/moment.min.js"></script>
      <script src="vendor/daterangepicker/daterangepicker.js"></script>
      <script src="vendor/countdowntime/countdowntime.js"></script>
      <script src="js/main.js"></script>
    </>
  );
};

export default CmpLogin;
