import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

function App() {
  let [name,setName] =useState('Anu') 
  let num1=10;
  let num2=20;
  let months=["Jan","Feb","Mar"];

  let clickme=()=>{
    setName("Anju")

  }

  return (
    <div className="App">
      {name}
      <h1>Hello { name } </h1>
      <h3>Result : { num1+num2 } </h3>
      <h3>Months: { months[0] } </h3>
      {
        months.map((months,index)=>(
          <h3>{index+1} - {months} </h3>
        ))

      }

      <h3>select a month:</h3>
      <select>
        {months.map((month, index) =>
        <option value={month} >
          {month}
        </option>
        )}

      </select>
      <button onClick={clickme}>Click me</button>
    </div>
  );
}

export default App;
