import React, { useState, useEffect } from 'react';
//import { useParams } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import { useParams } from 'react-router-dom';

const ShowUserSingleBill = () => {

    const location = useLocation();
      const params = new URLSearchParams(location.search);
      const username = params.get('username'); 
      const { echs_member_id } = useParams();  
  //const { username } = useParams(); // Get the username from the URL
  const [memberData, setMemberData] = useState({
    official_name: "",
    echs_card_no: "",
    bill_month: "",
    bill_year: "",
    bank_ifsc_code: "",
    bank_account_no: "",
    aadhar_no: "",
    bills: [], // Initialize an empty array for bills
    total_amount: 0 // Initialize total_amount as 0
  });

  const [error, setError] = useState(null);

  // Fetch the member details and bill items
  useEffect(() => {
    const fetchMemberDetails = async () => {
      try {
        console.log(`Fetching data for username: ${username}`); // Log to verify the username is passed correctly
        const response = await fetch(`http://localhost/echs_app/getsingleuserbill.php?echs_member_id=${echs_member_id}`);
        
        if (!response.ok) {
          throw new Error("Failed to fetch member details");
        }

        const data = await response.json();
        console.log("API Response: ", data); // Log the API response to check its structure

        // Check if the data is in the expected format
        if (data && data.official_name) {
          setMemberData({
            official_name: data.official_name,
            echs_card_no: data.echs_card_no,
            bill_month: data.bill_month,
            bill_year: data.bill_year,
            bank_ifsc_code: data.bank_ifsc_code,
            bank_account_no: data.bank_account_no,
            aadhar_no: data.aadhar_no,
            bills: data.bills || [],
            total_amount: data.total_amount || 0 // Ensure total_amount is set
          });
        } else {
          throw new Error("Invalid data structure received from API.");
        }
      } catch (err) {
        setError(err.message);
        console.error("Error fetching data:", err); // Log the error for debugging
      }
    };

    fetchMemberDetails();
  }, [echs_member_id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMemberData({ ...memberData, [name]: value });
  };

  // Error handling and rendering
  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container">
      <h1>Single User Bill Details</h1>
      <form>
        <div>
          <label>Official Name:</label>
          <input
            className="form-control"
            type="text"
            name="official_name"
            value={memberData.official_name || ""}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>ECHS Card No:</label>
          <input
            className="form-control"
            type="text"
            name="echs_card_no"
            value={memberData.echs_card_no || ""}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Bank Account No:</label>
          <input
            className="form-control"
            type="text"
            name="bank_account_no"
            value={memberData.bank_account_no || ""}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>IFSC Code:</label>
          <input
            className="form-control"
            type="text"
            name="bank_ifsc_code"
            value={memberData.bank_ifsc_code || ""}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Aadhar No:</label>
          <input
            className="form-control"
            type="text"
            name="aadhar_no"
            value={memberData.aadhar_no || ""}
            onChange={handleInputChange}
            required
          />
        </div>

        <h3>Bill Details</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Bill No</th>
              <th>Bill Date</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {memberData.bills && memberData.bills.length > 0 ? (
              memberData.bills.map((bill, index) => (
                <tr key={index}>
                  <td>{bill.billNo}</td>
                  <td>{bill.billDate}</td>
                  <td>{bill.amount}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3">No bills found</td>
              </tr>
            )}
          </tbody>
        </table>

        <h4>Total Amount: {memberData.total_amount}</h4>
      </form>
    </div>
  );
};

export default ShowUserSingleBill;
