import logo from './logo.svg';

import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

import TodoList from './TodoList';
import Home from './Home';
import About from './About';
import Service from './Service';
import Layout from './Layout';
import Registration from './pages/Registration';
import PropertyExample from './tutorials/PropertyExample';
import UseContextExample from './tutorials/UseContextExample';
import UseEffectExample from './tutorials/UseEffectExample';

function App() {
  let [name,setName] =useState('Anu') 
  let [counter,setCounter] =useState('0')
  let num1=80;
  let num2=20;
  let months=["Jan","Feb","Mar"];

  let clickme=()=>{
    setName("Anju")

  }

  let add=()=>{
    setCounter(counter++)
  }

  return (
    <div className="App">
      <BrowserRouter>
      <Routes>
      <Route path='/' element={<Layout/>} >
      <Route index element={<Home/>}/>
      <Route path='/about' element={<About/>}/>
      <Route path='/service' element={<Service/>}/>
      <Route path='/registration' element={<Registration/>}/>
      <Route path='/react-properties' element={<PropertyExample/>}/>
      <Route path='/react-usecontext' element={<UseContextExample/>}/>
      <Route path='/react-useeffect' element={<UseEffectExample/>}/>
      <Route path='/react-conditional-rendering' element={<ConditionRendering/>}/>

      </Route>

      </Routes>
        </BrowserRouter>
      {name}
      <h1>Hello { name } </h1>
      <h3>Result : { num1+num2 } </h3>
      <h3>Months: { months[0] } </h3>
      {
        months.map((months,index)=>(
          <h3>{index+1} - {months} </h3>
        ))

      }

      <h3>select a month:</h3>
      <select>
        {months.map((month, index) =>
        <option value={month} >
          {month}
        </option>
        )}

      </select>
      <button onClick={clickme}>Click me</button>
      <button onClick={add}>Counter{counter}</button>

      <TodoList></TodoList>
    </div>
  );
}

export default App;
