import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const ShowUserSingleBill = () => {
  const { echs_member_id } = useParams();
  const [memberData, setMemberData] = useState({
    official_name: "",
    echs_card_no: "",
    bill_month: "",
    bill_year: "",
    bank_ifsc_code: "",
    bank_account_no: "",
    aadhar_no: "",
    bills: [],
  });
  const [error, setError] = useState(null);
  const [totalAmount, setTotalAmount] = useState(0);

  // Fetch member and bill details
  useEffect(() => {
    const fetchMemberDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost/echs_app/getsingleuserbill.php?echs_member_id=${echs_member_id}`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch member details");
        }
        const data = await response.json();

        // Calculate total amount
        const total = data.bills.reduce((acc, bill) => acc + parseFloat(bill.amount), 0);

        setMemberData({
          official_name: data.official_name,
          echs_card_no: data.echs_card_no,
          bill_month: data.bill_month,
          bill_year: data.bill_year,
          bank_ifsc_code: data.bank_ifsc_code,
          bank_account_no: data.bank_account_no,
          aadhar_no: data.aadhar_no,
          bills: data.bills,
        });

        setTotalAmount(total);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchMemberDetails();
  }, [echs_member_id]);

  return (
    <div className="container">
      <h1>Single User Bill Details</h1>
      {error && <p className="text-danger">{error}</p>}
      {!error && (
        <>
          <div>
            <p><strong>Official Name:</strong> {memberData.official_name}</p>
            <p><strong>ECHS Card No:</strong> {memberData.echs_card_no}</p>
            <p>
              <strong>Bill Month:</strong> {memberData.bill_month}{" "}
              <strong>Bill Year:</strong> {memberData.bill_year}
            </p>
            <p><strong>Bank Account No:</strong> {memberData.bank_account_no}</p>
            <p><strong>Bank IFSC Code:</strong> {memberData.bank_ifsc_code}</p>
            <p><strong>Aadhar No:</strong> {memberData.aadhar_no}</p>
          </div>

          <h3>Bill Details</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Bill No</th>
                <th>Bill Date</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              {memberData.bills.map((bill, index) => (
                <tr key={index}>
                  <td>{bill.billNo}</td>
                  <td>{bill.billDate}</td>
                  <td>{bill.amount.toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="2" className="text-end"><strong>Total:</strong></td>
                <td>{totalAmount.toFixed(2)}</td>
              </tr>
            </tfoot>
          </table>
        </>
      )}
    </div>
  );
};

export default ShowUserSingleBill;
