import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

const UsePramsExample = () => {
    const url_params =useParams();
    let [product,setProduct]= useState();
    let get_product_by_id=(product_id)=>{
        fetch('https://dummyjson.com/products/'+product_id)
        .then(res =>res.json())
        .then(result=>{
            setProduct(result);
    });
    }
    useEffect(()=>{
        get_product_by_id(url_params.pid);
    },[]);
  return (
    <div>UsePramsExample
        {JSON.stringify(product)}

<div class="row">
    <div class="col-md-6">
        <img src={product.thumbnali}/>

    </div>
    <div class="col-md-6">
        <h2>{product.title}</h2>
        <p>
            {}
        </p>
    </div>
    </div>
    </div>
  )
}

export default UsePramsExample;
