import React, { useEffect, useState} from 'react';

export const UseEffectExample = () => {
    const [color,setColor]=useState('')
    useEffect(()=>{
        setColor("red");
       
    },[]);
  return (
    <div>UseEffectExample
    <h2>Set Color value after render</h2>
    <b>Color : {color}</b>
    </div>
  )
}

export default UseEffectExample
